import axios from "axios";
export default {
  data() {
    return {
      itemName: ""
    };
  },
  mounted() {
    this.itemName = this.name;
  },
  props: {
    id: {
      type: Number,
      required: true
    },
    name: {
      type: String,
      required: true
    }
  },
  methods: {
    updateItem() {
      axios.put(`api/items/${this.id}`, {
        id: this.id,
        name: this.itemName
      });
    }
  },
  watch: {
    value(newValue) {
      this.itemName = newValue;
    }
  }
};
